mapper.select:
select

from cloud_follow_user
where  user_id = #{userId,jdbcType=VARCHAR} AND follow_user_id = #{followUserId,jdbcType=VARCHAR}

select count(1)
from cloud_follow_user
where user_id = #{userId,jdbcType=VARCHAR}
  AND follow_user_id = #{followUserId,jdbcType=VARCHAR}
  and status = '1'

mapper.delete:
delete from cloud_follow_user
where  user_id = #{userId,jdbcType=VARCHAR}
  AND follow_user_id = #{followUserId,jdbcType=VARCHAR}

mapper.update:
update cloud_follow_user


            status = #{status,jdbcType=CHAR},


where   user_id = #{userId,jdbcType=VARCHAR} AND follow_user_id = #{followUserId,jdbcType=VARCHAR}

update cloud_follow_user
set
    status =  #{status,jdbcType=CHAR}
where   user_id = #{userId,jdbcType=VARCHAR} AND follow_user_id = #{followUserId,jdbcType=VARCHAR}
